Float->Float xy() {
    Float x { return y; }  //compiler error: y is not referenceable
    Float y { return x; }
    return x->y;
}

class Point() {
    Float x { return y; }  //compiler error: y is not referenceable
    Float y { return x; }
    Float->Float xy = x->y;
}

