shared void welcome(User? user) {
    if (exists user) {
        log.info("Hi " user.name "!");
    }
    else {
        log.info("Hello, World!");
    }
}

shared Payment payment(Order order) {
    if (exists Payment p = order.payment) {
        return p;
    }
    else { 
        return Payment(order);
    }
}

if (exists Payment p = order.payment) {
    if (p.paid) { 
        log.info("already paid"); 
    }
}

if (is CardPayment p = order.payment) {
    return p.card;
}

List<Element> list = ...;
Element element = ...;
if (satisfies Comparable<Element> Element) {
    Element test;
    if (exists Element first = list.first) {
        test = max(element, first);
    }
    else {
        test = element;
    }
    return forAll(Element e in list) every (e<=test);
}

shared PaymentProcessor processor {
    switch (payment.type) 
    case (null) { 
        throw NoPaymentTypeException();
    }
    case (credit, debit) { 
        return cardPaymentProcessor;
    }
    case (check) { 
        return checkPaymentProcessor;
    }
    else { 
        return interactivePaymentProcessor;
    }
}

switch (payment.type) {
    case (credit, debit) { 
        log.debug("card payment");
        cardPaymentProcessor.process(payment, user.card);
    } 
    case (check) {
        log.debug("check payment");
        checkPaymentProcessor.process(payment);
    }
    else {
        log.debug("other payment type");
    }
}

switch (payment) {
    case (is CardPayment) { 
        pay(payment.amount, payment.card);
    } 
    case (is CheckPayment) {
        pay(payment.amount, payment.check);
    }
    else {
        log.debug("other payment type");
    }
}

switch (node) {
    case (root) { 
        handleRootNode();
    } 
    case (is Leaf) {
        handleLeafNode(node);
    }
    case (is Branch) {
        handleBranchNode(node);
    }
}

shared void print<Printable>(Printable printable) 
        given Value of String | Integer | Float {
    switch (printable)
    case (is String) { 
        stream.writeLine(printable); 
    }
    case (is Integer) { 
        print($printable); 
    }
    case (is Float) { 
        print( FloatFormat(printable, 2) ); 
    }
}

shared Value join<Value>(Value x, Value y) 
        given Value of String | Integer {
    switch (Value)
    case (satisfies String) { 
        return x+y;
    }
    case (satisfies Integer) { 
        return (IntegerFormat(x,2)+IntegerFormat(y,2)).parseInteger(2);
    }
}

listitem.para:
if the iterated expression is also of type Sequence<X>,
                    the compiler is permitted to optimize away the use of Iterator, 
                    instead using indexed element access.

